import React, { useState, useEffect, useRef } from 'react';
import FullCalendar from '@fullcalendar/react';
import resourceTimeGridPlugin from '@fullcalendar/resource-timegrid';
import interactionPlugin, { Draggable } from '@fullcalendar/interaction';
import axios from 'axios';

export default function App() {
  const [resources, setResources] = useState([]);
  const [scheduled, setScheduled] = useState([]);
  const [unscheduled, setUnscheduled] = useState([]);
  const calendarRef = useRef(null);

  useEffect(() => {
    setResources([
      { id: '1', title: 'Machine 1' },
      { id: '2', title: 'Machine 2' }
    ]);
    axios.get('http://localhost:5000/api/orders').then(res => {
      const all = res.data;
      setScheduled(all.filter(o => o.start && o.end && o.resourceId));
      setUnscheduled(all.filter(o => !o.start || !o.end));
    });
  }, []);

  useEffect(() => {
    new Draggable(document.getElementById('unscheduled'), {
      itemSelector: '.fc-event',
      eventData: el => ({ id: el.dataset.id, title: el.dataset.title })
    });
  }, [unscheduled]);

  const snap30 = date => {
    const d = new Date(date);
    d.setMinutes(Math.round(d.getMinutes()/30)*30);
    return d;
  };

  const onReceive = info => {
    const ev = info.event;
    const start = snap30(ev.start);
    const end = new Date(start.getTime() + 30*60000);
    ev.setStart(start); ev.setEnd(end);
    const rid = ev.getResources()[0].id;
    axios.put(`http://localhost:5000/api/orders/${ev.id}`, { start: start.toISOString(), end: end.toISOString(), resourceId: rid });
    setUnscheduled(u => u.filter(x=>x.id!==ev.id));
    setScheduled(s=>[...s,{ id:ev.id,title:ev.title,start:end.toISOString(),end:end.toISOString(),resourceId:rid }]);
  };

  const onDrop = info => {
    const ev = info.event;
    const start = snap30(ev.start);
    const end = new Date(start.getTime() + (ev.end - ev.start));
    ev.setStart(start); ev.setEnd(end);
    const rid = ev.getResources()[0].id;
    axios.put(`http://localhost:5000/api/orders/${ev.id}`, { start: start.toISOString(), end: end.toISOString(), resourceId: rid });
  };

  return (
    <div style={{ display:'flex' }}>
      <div id="unscheduled" style={{ width:200,padding:10,borderRight:'1px solid #ddd' }}>
        <h3>Unscheduled</h3>
        {unscheduled.map(o=><div key={o.id} className="fc-event" data-id={o.id} data-title={o.title} style={{margin:'5px 0',padding:'4px',background:'#3174ad',color:'#fff',cursor:'pointer'}}>{o.title}</div>)}
      </div>
      <div style={{ flex:1,padding:10 }}>
        <FullCalendar
          ref={calendarRef}
          plugins={[ resourceTimeGridPlugin, interactionPlugin ]}
          initialView="resourceTimeGridWeek"
          editable droppable
          resources={resources}
          events={scheduled}
          eventReceive={onReceive}
          eventDrop={onDrop}
          slotDuration="00:30:00"
          resourceAreaHeaderContent="Machines"
        />
      </div>
    </div>
  );
}
