// File: frontend/src/App.js

import React, { useEffect, useState, useCallback } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import axios from 'axios';

// CONFIGURATION
const API_ROOT       = 'http://localhost:5000/api';
const WORK_START_HR  = 8,  WORK_START_MIN = 30;
const WORK_END_HR    = 16, WORK_END_MIN   = 30;
const WEEKENDS       = [0,6];
const HOLIDAYS       = ['2025-01-01','2025-12-25'];

// COLOR CONSTANTS
const LIGHT_YELLOW  = '#FFF9C4', DARK_YELLOW  = '#FDD835';
const LIGHT_GREY    = '#ECEFF1', DARK_GREY    = '#616161';
const LIGHT_PURPLE  = '#E1BEE7', DARK_PURPLE  = '#6A1B9A';
const BUBBLE_START  = '#e0f7fa';
const BUBBLE_END    = '#ffe0b2';
const BUBBLE_DELIV  = '#c8e6c9';

// —————————————————————————————
// HELPERS
// —————————————————————————————
function isHoliday(dt) {
  return dt instanceof Date &&
         !isNaN(dt) &&
         HOLIDAYS.includes(dt.toISOString().slice(0,10));
}
function isWorkday(dt) {
  return dt instanceof Date &&
         !isNaN(dt) &&
         !WEEKENDS.includes(dt.getDay()) &&
         !isHoliday(dt);
}
function clampToWorkHours(dt) {
  let d = new Date(dt);
  while (
    !isWorkday(d) ||
    d.getHours() < WORK_START_HR ||
    (d.getHours() === WORK_START_HR && d.getMinutes() < WORK_START_MIN)
  ) {
    d.setDate(d.getDate() + 1);
    d.setHours(WORK_START_HR, WORK_START_MIN, 0, 0);
  }
  if (
    d.getHours() > WORK_END_HR ||
    (d.getHours() === WORK_END_HR && d.getMinutes() >= WORK_END_MIN)
  ) {
    d.setDate(d.getDate() + 1);
    d.setHours(WORK_START_HR, WORK_START_MIN, 0, 0);
    return clampToWorkHours(d);
  }
  return d;
}
function addWorkTime(start, ms) {
  let remaining = ms;
  let current = clampToWorkHours(start);
  while (remaining > 0) {
    const endOfDay = new Date(current);
    endOfDay.setHours(WORK_END_HR, WORK_END_MIN, 0, 0);
    const free = endOfDay.getTime() - current.getTime();
    if (free <= 0) {
      current = clampToWorkHours(new Date(current.setDate(current.getDate() + 1)));
    } else if (remaining <= free) {
      current = new Date(current.getTime() + remaining);
      remaining = 0;
    } else {
      remaining -= free;
      current = new Date(endOfDay);
    }
  }
  return current;
}
function fmtDT(dt) {
  const pad = n => String(n).padStart(2,'0');
  let h = dt.getHours(), m = pad(dt.getMinutes()), ap = h >= 12 ? 'PM' : 'AM';
  h = h % 12 || 12;
  return `${pad(dt.getMonth()+1)}/${pad(dt.getDate())} ${pad(h)}:${m} ${ap}`;
}
function parseDueDate(d) {
  if (!d) return null;
  if (/^\d{4}-\d{2}-\d{2}$/.test(d)) return new Date(d);
  const parts = d.split('/');
  if (parts.length >= 2) {
    const mo = +parts[0], da = +parts[1],
          yr = parts[2] ? +parts[2] : new Date().getFullYear();
    if (!isNaN(mo)&&!isNaN(da)&&!isNaN(yr)) return new Date(yr,mo-1,da);
  }
  const dt = new Date(d);
  return isNaN(dt) ? null : dt;
}
function addWorkDays(start, days) {
  let d = new Date(start), added = 0;
  while (added < days) {
    d.setDate(d.getDate() + 1);
    if (isWorkday(d)) added++;
  }
  return d;
}
function subWorkDays(start, days) {
  let d = new Date(start), removed = 0;
  while (removed < days) {
    d.setDate(d.getDate() - 1);
    if (isWorkday(d)) removed++;
  }
  return d;
}
function fmtMMDD(d) {
  const dt = new Date(d);
  return `${String(dt.getMonth()+1).padStart(2,'0')}/${String(dt.getDate()).padStart(2,'0')}`;
}
function sortQueue(arr) {
  return [...arr].sort((a,b) => {
    const da = parseDueDate(a.due_date), db = parseDueDate(b.due_date);
    if (da && db) return da - db;
    if (da) return -1;
    if (db) return 1;
    return 0;
  });
}

// —————————————————————————————
// SCHEDULING + LATE
// —————————————————————————————
function scheduleMachineJobs(jobs) {
  let prevEnd = null;
  return jobs.map((job, idx) => {
    let start;
    if (idx === 0) {
      start = job.embroidery_start
        ? clampToWorkHours(new Date(job.embroidery_start))
        : clampToWorkHours(new Date());
      if (!job.embroidery_start) job.embroidery_start = new Date().toISOString();
    } else {
      start = clampToWorkHours(new Date(prevEnd.getTime() + 30*60000));
    }
    const qty = job.quantity % 6 === 0
      ? job.quantity
      : Math.ceil(job.quantity/6) * 6;
    const runMs = (job.stitchCount/30000) * (qty/6) * 3600000;
    const end   = addWorkTime(start, runMs);

    const cutoff = new Date(subWorkDays(parseDueDate(job.due_date),6));
    cutoff.setHours(WORK_END_HR, WORK_END_MIN, 0, 0);

    job._rawStart = start;
    job._rawEnd   = end;
    job.start     = fmtDT(start);
    job.end       = fmtDT(end);
    job.delivery  = fmtMMDD(addWorkDays(end,6));
    job.isLate    = end > cutoff;

    prevEnd = end;
    return job;
  });
}

// —————————————————————————————
// LINK UTILITIES
// —————————————————————————————
function loadLinks() {
  try {
    return JSON.parse(localStorage.getItem('jobLinks') || '{}');
  } catch {
    return {};
  }
}
function saveLinks(map) {
  localStorage.setItem('jobLinks', JSON.stringify(map));
}
function getChain(jobs, id) {
  const fwd = {}, rev = {};
  jobs.forEach(j => {
    if (j.linkedTo) {
      fwd[j.id]       = j.linkedTo;
      rev[j.linkedTo] = j.id;
    }
  });
  let root = id;
  while (rev[root]) root = rev[root];
  const chain = [];
  let cur = root;
  while (cur) {
    chain.push(cur);
    cur = fwd[cur];
  }
  return chain;
}

// —————————————————————————————
// MAIN COMPONENT
// —————————————————————————————
export default function App() {
  const [columns, setColumns] = useState({
    queue:    { id:'queue',    title:'Unscheduled', jobs:[] },
    machine1: { id:'machine1', title:'Machine 1',   jobs:[] },
    machine2: { id:'machine2', title:'Machine 2',   jobs:[] },
  });

  const [placeholders, setPlaceholders] = useState(() =>
    JSON.parse(localStorage.getItem('placeholders') || '[]')
  );
  useEffect(() => {
    localStorage.setItem('placeholders', JSON.stringify(placeholders));
  }, [placeholders]);

  const [links, setLinks] = useState(loadLinks);
  useEffect(() => { saveLinks(links); }, [links]);

  const [showModal, setShowModal] = useState(false);
  const [ph, setPh] = useState({
    id:null, company:'', quantity:'', stitchCount:'', inHand:'', dueType:'Hard Date'
  });
  const [syncStatus, setSyncStatus] = useState('');

  const fetchAll = useCallback(async () => {
    const { data } = await axios.get(`${API_ROOT}/orders`);
    const real = { queue:[], machine1:[], machine2:[] };

    data.forEach(o => {
      if (!o.title) return;
      const col = (o.machineId==='machine1'||o.machineId==='machine2')
        ? o.machineId : 'queue';
      real[col].push({
        ...o,
        id: o.id.toString(),
        stitchCount: o.stitch_count,
        quantity: o.quantity,
        company: o.company,
        design: o.design,
        due_date: o.due_date,
        due_type: o.due_type,
        start: o.start_date || '',
        end: o.end_date   || '',
        delivery: o.delivery   || '',
        position: o.position != null ? o.position : null,
        embroidery_start: o.embroidery_start || '',
        linkedTo: links[o.id] || null
      });
    });

    ['machine1','machine2'].forEach(c => {
      real[c].sort((a,b) => (a.position || Infinity) - (b.position || Infinity));
    });

    const merged = {};
    ['queue','machine1','machine2'].forEach(c => {
      merged[c] = [
        ...placeholders.filter(p => p.machineId===c),
        ...real[c]
      ];
    });

    setColumns({
      queue:    { ...columns.queue,    jobs: sortQueue(merged.queue) },
      machine1: { ...columns.machine1, jobs: scheduleMachineJobs(merged.machine1) },
      machine2: { ...columns.machine2, jobs: scheduleMachineJobs(merged.machine2) },
    });
  }, [links, placeholders]);

  useEffect(() => { fetchAll().catch(console.error); }, [fetchAll]);

  const handleSync = () => {
    fetchAll().catch(console.error);
    setSyncStatus('updated');
    setTimeout(() => setSyncStatus(''), 2000);
  };

  const submitPlaceholder = () => {
    const job = {
      id: ph.id || `ph-${Date.now()}`,
      company: ph.company,
      quantity: +ph.quantity || 1,
      stitchCount: +ph.stitchCount || 30000,
      due_date: ph.inHand,
      due_type: ph.dueType,
      machineId: 'queue',
      position: null
    };
    if (ph.id) {
      setPlaceholders(ps => ps.map(p => p.id===ph.id ? job : p));
      setColumns(c => ({
        ...c,
        queue:    {...c.queue,    jobs: c.queue.jobs.map(j => j.id===ph.id ? job : j)},
        machine1: {...c.machine1, jobs: c.machine1.jobs.map(j => j.id===ph.id ? job : j)},
        machine2: {...c.machine2, jobs: c.machine2.jobs.map(j => j.id===ph.id ? job : j)},
      }));
    } else {
      setPlaceholders(ps => [job, ...ps]);
      setColumns(c => ({
        ...c,
        queue: {...c.queue, jobs: [job, ...c.queue.jobs]}
      }));
    }
    setShowModal(false);
    setPh({ id:null, company:'', quantity:'', stitchCount:'', inHand:'', dueType:'Hard Date' });
  };

  const removePlaceholder = id => {
    setPlaceholders(ps => ps.filter(p => p.id!==id));
    setColumns(c => ({
      ...c,
      queue:    {...c.queue,    jobs: c.queue.jobs.filter(j => j.id!==id)},
      machine1: {...c.machine1, jobs: c.machine1.jobs.filter(j => j.id!==id)},
      machine2: {...c.machine2, jobs: c.machine2.jobs.filter(j => j.id!==id)},
    }));
  };

  const editPlaceholder = job => {
    setPh({
      id: job.id,
      company: job.company,
      quantity: job.quantity,
      stitchCount: job.stitchCount,
      inHand: job.due_date,
      dueType: job.due_type
    });
    setShowModal(true);
  };

  const toggleLink = (colId, idx) => {
    setColumns(c => {
      const jobs = [...c[colId].jobs];
      const job  = jobs[idx];
      const below= jobs[idx+1];
      if (!below) return c;
      setLinks(l => {
        const m = {...l};
        if (m[job.id] === below.id) delete m[job.id];
        else m[job.id] = below.id;
        return m;
      });
      job.linkedTo = job.linkedTo===below.id ? null : below.id;
      jobs[idx] = job;
      return {...c, [colId]:{...c[colId], jobs}};
    });
  };

  const onDragEnd = result => {
    const { source, destination, draggableId, combine } = result;
    if (combine || !destination) return;
    const sc = source.droppableId, dc = destination.droppableId;
    if (sc===dc && source.index===destination.index) return;

    const chain = getChain(columns[sc].jobs, draggableId);
    const srcJobs = columns[sc].jobs.filter(j => !chain.includes(j.id));
    let destJobs = [...columns[dc].jobs];
    if (sc===dc) {
      destJobs = srcJobs.slice();
      destJobs.splice(destination.index, 0,
        ...columns[sc].jobs.filter(j => chain.includes(j.id))
      );
    } else {
      destJobs.splice(destination.index, 0,
        ...columns[sc].jobs.filter(j => chain.includes(j.id))
      );
    }

    setPlaceholders(ps =>
      ps.map(p => chain.includes(p.id) ? {...p, machineId: dc} : p)
    );

    const updated = {...columns};
    updated[sc].jobs = srcJobs;
    updated[dc].jobs = destJobs;

    chain.forEach((id,i) => {
      const job = updated[dc].jobs.find(j => j.id===id);

      if (id.startsWith('ph-')) {
        job._rawStart = job._rawEnd = null;
        job.start = job.end = job.delivery = '';
        job.isLate = false;
        job.machineId = dc;
        return;
      }

      if (dc==='queue') {
        job._rawStart = job._rawEnd = null;
        job.start = job.end = job.delivery = '';
        job.isLate = false;
        job.embroidery_start = '';
        job.position = null;
      }

      job.machineId = dc==='queue'?null:dc;
      job.position  = dc==='queue'?null:(destination.index+i);

      axios.put(`${API_ROOT}/orders/${id}`, {
        machineId: job.machineId,
        start_date: job.start||'', 
        end_date:   job.end  ||'', 
        delivery:   job.delivery||'',
        position:   job.position,
        embroidery_start: job.embroidery_start||''
      }).catch(console.error);
    });

    updated.machine1.jobs = scheduleMachineJobs(updated.machine1.jobs);
    updated.machine2.jobs = scheduleMachineJobs(updated.machine2.jobs);
    updated.queue.jobs    = sortQueue(updated.queue.jobs);

    setColumns(updated);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div style={{ padding:16, fontFamily:'sans-serif', fontSize:14 }}>
        <button onClick={() => setShowModal(true)} style={{ marginRight:8 }}>
          + Add Placeholder
        </button>
        <button onClick={handleSync}>
          Sync from Sheet {syncStatus==='updated' && <span style={{ color:'green' }}>✓ Updated</span>}
        </button>

        {showModal && (
          <div style={{
            position:'fixed', top:0, left:0, right:0, bottom:0,
            background:'rgba(0,0,0,0.3)',
            display:'flex', alignItems:'center', justifyContent:'center',
            zIndex:1000
          }}>
            <div style={{ background:'#fff', padding:20, borderRadius:4, minWidth:300 }}>
              <h3 style={{ marginTop:0, textAlign:'center' }}>Add/Edit Placeholder</h3>
              <label>Company<br/>
                <input
                  value={ph.company}
                  onChange={e => setPh({...ph,company:e.target.value})}
                  style={{ width:'100%' }}
                />
              </label><br/><br/>
              <label>In Hand Date (IHD)<br/>
                <input
                  type="date"
                  value={ph.inHand}
                  onChange={e => setPh({...ph,inHand:e.target.value})}
                  style={{ width:'100%' }}
                />
              </label><br/><br/>
              <label>Quantity<br/>
                <input
                  type="number"
                  value={ph.quantity}
                  onChange={e => setPh({...ph,quantity:e.target.value})}
                  style={{ width:'100%' }}
                />
              </label><br/><br/>
              <label>Stitch Count<br/>
                <input
                  type="number"
                  value={ph.stitchCount}
                  onChange={e => setPh({...ph,stitchCount:e.target.value})}
                  style={{ width:'100%' }}
                />
              </label><br/><br/>
              <label>Due Type<br/>
                <select
                  value={ph.dueType}
                  onChange={e => setPh({...ph,dueType:e.target.value})}
                  style={{ width:'100%' }}
                >
                  <option>Hard Date</option>
                  <option>Soft Date</option>
                </select>
              </label><br/><br/>
              <div style={{ textAlign:'right' }}>
                <button onClick={() => setShowModal(false)} style={{ marginRight:8 }}>Cancel</button>
                <button onClick={submitPlaceholder}>Submit</button>
              </div>
            </div>
          </div>
        )}

        <div style={{ display:'flex', gap:16, marginTop:16 }}>
          {['queue','machine1','machine2'].map(colId => {
            const col = columns[colId];
            return (
              <Droppable key={colId} droppableId={colId}>
                {provided => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    style={{
                      border:'1px solid #ccc',
                      borderRadius:4,
                      width:260,
                      minHeight:400,
                      padding:12,
                      background:'#fafafa'
                    }}
                  >
                    <h4 style={{ textAlign:'center', margin:'8px 0' }}>{col.title}</h4>

                    {col.jobs.map((job, idx) => {
                      const isPh   = job.id.startsWith('ph-');
                      const isSoft = job.due_type === 'Soft Date';
                      const base   = isPh ? LIGHT_YELLOW : (isSoft ? LIGHT_GREY : LIGHT_PURPLE);
                      const stripe = 'rgba(255,0,0,0.5)';
                      let bg       = base;
                      let border   = `2px solid ${isPh?DARK_YELLOW:(isSoft?DARK_GREY:DARK_PURPLE)}`;
                      if (job.isLate) {
                        bg     = `repeating-linear-gradient(45deg, ${base}, ${base} 6px, ${stripe} 6px, ${stripe} 12px)`;
                        border = '2px solid red';
                      }
                      const belowLinked = job.linkedTo === col.jobs[idx+1]?.id;
                      const aboveLinked = col.jobs[idx-1]?.linkedTo === job.id;

                      return (
                        <Draggable key={job.id} draggableId={job.id} index={idx}>
                          {p => (
                            <div
                              ref={p.innerRef}
                              {...p.draggableProps}
                              {...p.dragHandleProps}
                              style={{
                                position:'relative',
                                display:'grid',
                                gridTemplateColumns:'140px 100px',
                                gridTemplateRows:'auto auto auto auto',
                                columnGap:8,
                                rowGap:4,
                                padding:6,
                                margin:'0 0 6px 0',
                                background:bg,
                                border:border,
                                borderRadius:4,
                                ...p.draggableProps.style
                              }}
                            >
                              {/* Row1 Col1 */}
                              <span style={{
                                gridRow:1, gridColumn:1,
                                display:'inline-flex', alignItems:'center',
                                background:base, padding:'2px 6px', borderRadius:4,
                                width:'max-content'
                              }}>
                                <span style={{
                                  flex:'0 0 20px',
                                  height:20,
                                  borderRadius:'50%',
                                  background:'#000',
                                  display:'flex',
                                  alignItems:'center',
                                  justifyContent:'center',
                                  fontSize:isPh?14:12,
                                  lineHeight:'20px',
                                  fontWeight:'bold', color:base,
                                  marginRight:6
                                }}>{isPh?'*':job.id}</span>
                                <span style={{
                                  fontWeight:'bold',
                                  fontSize:14,
                                  whiteSpace:'nowrap',
                                  overflow:'hidden',
                                  textOverflow:'ellipsis'
                                }}>{job.company}</span>
                              </span>
                              {/* Row1 Col2 */}
                              <span style={{
                                gridRow:1, gridColumn:2,
                                justifySelf:'end',
                                background:base,
                                padding:'2px 4px',
                                borderRadius:4,
                                fontSize:12,
                                fontWeight:'bold',
                                width:'max-content'
                              }}>{job.due_type}</span>

                              {/* Row2 Col1 */}
                              <span style={{
                                gridRow:2, gridColumn:1,
                                background:base,
                                padding:'2px 6px',
                                borderRadius:4,
                                width:'max-content'
                              }}>
                                <span style={{
                                  fontSize:14,
                                  whiteSpace:'nowrap',
                                  overflow:'hidden',
                                  textOverflow:'ellipsis'
                                }}>{`${job.design} — ${job.quantity}`}</span>
                              </span>
                              {/* Row2 Col2 */}
                              {job.start && (
                                <span style={{
                                  gridRow:2, gridColumn:2,
                                  justifySelf:'end',
                                  background:BUBBLE_START,
                                  padding:'2px 4px',
                                  borderRadius:10,
                                  fontSize:12
                                }}>{job.start}</span>
                              )}

                              {/* Row3 Col1 */}
                              <span style={{
                                gridRow:3, gridColumn:1,
                                background:BUBBLE_END,
                                padding:'2px 4px',
                                borderRadius:4,
                                fontSize:12
                              }}>{`EED: ${fmtMMDD(subWorkDays(parseDueDate(job.due_date),6))}`}</span>
                              {belowLinked && (
                                <div style={{
                                  position:'absolute',
                                  bottom:-4,
                                  left:'50%',
                                  width:2,
                                  height:8,
                                  background:'#555'
                                }}/>
                              )}
                              {/* Row3 Col2 */}
                              {job.end && (
                                <span style={{
                                  gridRow:3, gridColumn:2,
                                  justifySelf:'end',
                                  background:BUBBLE_END,
                                  padding:'2px 4px',
                                  borderRadius:10,
                                  fontSize:12
                                }}>{job.end}</span>
                              )}

                              {/* Row4 Col1 */}
                              <span style={{
                                gridRow:4, gridColumn:1,
                                background:BUBBLE_DELIV,
                                padding:'2px 4px',
                                borderRadius:4,
                                fontSize:12
                              }}>{`IHD: ${fmtMMDD(job.due_date)}`}</span>
                              {aboveLinked && (
                                <div style={{
                                  position:'absolute',
                                  top:-4,
                                  left:'50%',
                                  width:2,
                                  height:8,
                                  background:'#555'
                                }}/>
                              )}
                              {/* Row4 Col2 */}
                              {job.delivery && (
                                <span style={{
                                  gridRow:4, gridColumn:2,
                                  justifySelf:'end',
                                  background:BUBBLE_DELIV,
                                  padding:'2px 4px',
                                  borderRadius:10,
                                  fontSize:12
                                }}>{job.delivery}</span>
                              )}

                              {/* Right‐side tab */}
                              <div style={{
                                position:'absolute',
                                top:0, right:-28,
                                width:28,
                                height:'100%',
                                display:'flex',
                                flexDirection:'column',
                                alignItems:'center',
                                justifyContent:'space-between',
                                background:base,
                                borderTopRightRadius:4,
                                borderBottomRightRadius:4,
                                boxShadow:'-2px 0 4px rgba(0,0,0,0.1)'
                              }}>
                                {isPh && (
                                  <div style={{ display:'flex', flexDirection:'column', alignItems:'center' }}>
                                    <span onClick={() => editPlaceholder(job)} style={{ cursor:'pointer', fontSize:12, margin:4 }}>✎</span>
                                    <span onClick={() => removePlaceholder(job.id)} style={{ cursor:'pointer', fontSize:12, margin:4 }}>✖</span>
                                  </div>
                                )}
                                <span onClick={() => toggleLink(colId,idx)} style={{ cursor:'pointer', fontSize:16, margin:4 }}>
                                  {job.linkedTo===col.jobs[idx+1]?.id ? '❌' : '🔗'}
                                </span>
                              </div>

                            </div>
                          )}
                        </Draggable>
                      );
                    })}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            );
          })}
        </div>
      </div>
    </DragDropContext>
  );
}
