function getChain(jobs, id) {
  const fwd = {}, rev = {};
  jobs.forEach(j => {
    if (j.linkedTo) {
      fwd[j.id]    = j.linkedTo;
      rev[j.linkedTo] = j.id;
    }
  });

  // find root of the chain
  let root = id;
  while (rev[root]) {
    root = rev[root];
  }

  // build the chain array
  const chain = [];
  let cur = root;
  while (cur) {
    chain.push(cur);
    cur = fwd[cur];
  }
  return chain;
}

// === Section 5: FETCH & MERGE (with Manual Assignment & Ordering) ===
const fetchAll = async () => {
  console.log('▶️ fetchAll: starting data load');

  try {
    // 1) Load manual state from localStorage
    const manualState = JSON.parse(
      localStorage.getItem('manualState') ||
      JSON.stringify({ machine1: [], machine2: [] })
    );
    console.log('  – manualState:', manualState);

    // 2) Preserve any existing embroidery_start
    const prevEmb = {};
    Object.values(columns)
      .flatMap(col => col.jobs)
      .forEach(j => {
        prevEmb[j.id] = j.embroidery_start || j.start_date || '';
      });

    // 3) Fetch orders + embroideryList
    const [{ data: orders }, { data: embList }] = await Promise.all([
      axios.get(`${API_ROOT}/orders`),
      axios.get(`${API_ROOT}/embroideryList`)
    ]);

    // 4) Build embroidery-start map
    const embMap = {};
    embList.forEach(row => {
      const id = String(row['Order #'] || '').trim();
      if (id) embMap[id] = row['Embroidery Start Time'] || '';
    });

    // 5) Build up jobById
    const jobById = {};
    orders.forEach(o => {
      if (o.id == null) return;
      const id    = String(o.id).trim();
      const rawTs = embMap[id] ?? prevEmb[id] ?? '';
      jobById[id] = {
        ...o,
        id,
        stitchCount: o.stitch_count,
        quantity:    o.quantity,
        company:     o.company,
        design:      o.design,
        due_date:    o.due_date,
        due_type:    o.due_type,
        embroidery_start: rawTs,
        start_date:  rawTs,
        linkedTo:    links[o.id] || null,
        // server-side machine assignment:
        machineId:   o.machineId ?? o.machine ?? ''
      };
    });

    // 6) Override with any manualState assignment
    ;['machine1','machine2'].forEach(col => {
      (manualState[col] || []).forEach(id => {
        if (jobById[id]) jobById[id].machineId = col;
      });
    });

    // 7) Build each machine in manual order + append new arrivals
    const buildMachine = colId => {
      const manualList = manualState[colId] || [];
      const existing   = manualList.filter(id => jobById[id]).map(id => jobById[id]);
      const appended   = Object.values(jobById)
        .filter(j => j.machineId === colId && !manualList.includes(j.id));
      return [...existing, ...appended];
    };
    const machine1Jobs = buildMachine('machine1');
    const machine2Jobs = buildMachine('machine2');

    // 8) Build queue: everything else, sorted by due date
    const queueJobs = Object.values(jobById)
      .filter(j => !['machine1','machine2'].includes(j.machineId))
      .map(j => ({ 
        ...j,
        start_date: '',
        _rawStart:  null,
        _rawEnd:    null,
        start:      '',
        end:        '',
        delivery:   '',
        isLate:     false
      }))
      .sort((a, b) => {
        const da = parseDueDate(a.due_date), db = parseDueDate(b.due_date);
        if (da && db) return da - db;
        if (da) return -1;
        if (db) return 1;
        return 0;
      });

    // 9) Schedule runtimes (machines only)
    const next = {
      queue:    { ...columns.queue,    jobs: queueJobs },
      machine1: { ...columns.machine1, jobs: scheduleMachineJobs(machine1Jobs) },
      machine2: { ...columns.machine2, jobs: scheduleMachineJobs(machine2Jobs) },
    };
    console.log('▶️ fetchAll: setting columns →', next);
    setColumns(next);

  } catch (err) {
    console.error('⚠️ fetchAll error:', err);
  }
};


