const toggleLink = (colId, idx) => {
  setColumns(cols => {
    // clone this columnâ€™s jobs
    const jobs = [...cols[colId].jobs];
    const job  = jobs[idx];
    const below = jobs[idx + 1];
    if (!below) return cols;  // nothing below to link to

    // update our persisted links map
    setLinks(l => {
      const m = { ...l };
      if (m[job.id] === below.id) delete m[job.id];
      else m[job.id] = below.id;
      return m;
    });

    // toggle the in-memory link flag
    job.linkedTo = job.linkedTo === below.id ? null : below.id;
    jobs[idx] = job;

    // return updated columns state
    return {
      ...cols,
      [colId]: { ...cols[colId], jobs }
    };
  });
};

// === Section 8: Drag & Drop Handler (with Manual Assignment/Order) ===
const onDragEnd = result => {
  const { source, destination, draggableId } = result;
  if (!destination) return;

  const srcId = source.droppableId;
  const dstId = destination.droppableId;
  // no-op if dropped back in same position
  if (srcId === dstId && source.index === destination.index) return;

  // 1) Load manualState
  const manualState = JSON.parse(
    localStorage.getItem('manualState') ||
    JSON.stringify({ machine1: [], machine2: [] })
  );

  // 2) If same machine, just reorder its array
  if (srcId === dstId && (srcId === 'machine1' || srcId === 'machine2')) {
    const arr = Array.from(manualState[srcId] || []);
    // find & remove
    const from = arr.indexOf(draggableId);
    if (from > -1) {
      arr.splice(from, 1);
      arr.splice(destination.index, 0, draggableId);
      manualState[srcId] = arr;
    }
  } else {
    // 3) Cross-column move: remove from any machine lists
    ['machine1','machine2'].forEach(col => {
      manualState[col] = (manualState[col] || []).filter(id => id !== draggableId);
    });
    // 4) If dropped into a machine, insert into that machineâ€™s manual list
    if (dstId === 'machine1' || dstId === 'machine2') {
      const arr = Array.from(manualState[dstId] || []);
      arr.splice(destination.index, 0, draggableId);
      manualState[dstId] = arr;
    }
  }

  // 5) Persist manualState
  localStorage.setItem('manualState', JSON.stringify(manualState));

  // 6) Now update UI immediately (same as before)
  const srcJobs = Array.from(columns[srcId].jobs);
  const [moved] = srcJobs.splice(source.index, 1);

  // clear schedule when moving into queue
  const jobForDest = dstId === 'queue'
    ? { ...moved, start_date:'', _rawStart:null, _rawEnd:null, start:'', end:'', delivery:'', isLate:false }
    : moved;

  const dstJobs = Array.from(columns[dstId].jobs);
  dstJobs.splice(destination.index, 0, jobForDest);

  const finalDst = dstId === 'queue'
    ? dstJobs.sort((a, b) => {
        const da = parseDueDate(a.due_date), db = parseDueDate(b.due_date);
        if (da && db) return da - db;
        if (da) return -1;
        if (db) return 1;
        return 0;
      })
    : scheduleMachineJobs(dstJobs);

  setColumns({
    ...columns,
    [srcId]: { ...columns[srcId], jobs: srcJobs },
    [dstId]: { ...columns[dstId], jobs: finalDst },
  });
};

