// === Section 1: Imports, Configuration & Component Setup ===
// File: frontend/src/App.js

import React, { useEffect, useState, useCallback } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';

// CONFIGURATION
const API_ROOT       = 'http://localhost:5000/api';
const WORK_START_HR  = 8,  WORK_START_MIN = 30;
const WORK_END_HR    = 16, WORK_END_MIN   = 30;
const WEEKENDS       = [0,6]; // Sunday=0, Saturday=6
const HOLIDAYS       = ['2025-01-01','2025-12-25'];

// COLOR CONSTANTS
const LIGHT_YELLOW  = '#FFF9C4', DARK_YELLOW  = '#FDD835';
const LIGHT_GREY    = '#ECEFF1', DARK_GREY    = '#616161';
const LIGHT_PURPLE  = '#E1BEE7', DARK_PURPLE  = '#6A1B9A';
const BUBBLE_START  = '#e0f7fa';
const BUBBLE_END    = '#ffe0b2';
const BUBBLE_DELIV  = '#c8e6c9';

// Start of your App component
export default function App() {
  // State hooks
  const [columns, setColumns]   = useState({
    queue:    { title: 'Queue',     jobs: [] },
    machine1: { title: 'Machine 1', jobs: [] },
    machine2: { title: 'Machine 2', jobs: [] },
  });
  const [links, setLinks]       = useState(loadLinks());
  const [syncStatus, setSyncStatus] = useState('');
  const [showModal, setShowModal]   = useState(false);
  const [ph, setPh]             = useState({
    id: null,
    company: '',
    quantity: '',
    stitchCount: '',
    inHand: '',
    dueType: 'Hard Date'
  });

  // Initial data fetch
  useEffect(() => {
    fetchAll();
  }, []);

  // Manual sync handler
  const handleSync = async () => {
    setSyncStatus('');
    await fetchAll();
    setSyncStatus('updated');
    setTimeout(() => setSyncStatus(''), 2000);
  };
// === Section 2: Helpers ===

function isHoliday(dt) {
  return dt instanceof Date &&
         !isNaN(dt) &&
         HOLIDAYS.includes(dt.toISOString().slice(0,10));
}

function isWorkday(dt) {
  return dt instanceof Date &&
         !isNaN(dt) &&
         !WEEKENDS.includes(dt.getDay()) &&
         !isHoliday(dt);
}

function clampToWorkHours(dt) {
  let d = new Date(dt);
  while (
    !isWorkday(d) ||
    d.getHours() < WORK_START_HR ||
    (d.getHours() === WORK_START_HR && d.getMinutes() < WORK_START_MIN)
  ) {
    d.setDate(d.getDate() + 1);
    d.setHours(WORK_START_HR, WORK_START_MIN, 0, 0);
  }
  if (
    d.getHours() > WORK_END_HR ||
    (d.getHours() === WORK_END_HR && d.getMinutes() >= WORK_END_MIN)
  ) {
    d.setDate(d.getDate() + 1);
    d.setHours(WORK_START_HR, WORK_START_MIN, 0, 0);
    return clampToWorkHours(d);
  }
  return d;
}

function addWorkTime(start, ms) {
  let remaining = ms;
  let current = clampToWorkHours(start);
  while (remaining > 0) {
    const endOfDay = new Date(current);
    endOfDay.setHours(WORK_END_HR, WORK_END_MIN, 0, 0);
    const free = endOfDay.getTime() - current.getTime();
    if (free <= 0) {
      current = clampToWorkHours(new Date(current.setDate(current.getDate() + 1)));
    } else if (remaining <= free) {
      current = new Date(current.getTime() + remaining);
      remaining = 0;
    } else {
      remaining -= free;
      current = new Date(endOfDay);
    }
  }
  return current;
}

function fmtDT(dt) {
  const pad = n => String(n).padStart(2,'0');
  const month = pad(dt.getMonth() + 1);
  const day   = pad(dt.getDate());
  let h = dt.getHours(), m = pad(dt.getMinutes()), ap = h >= 12 ? 'PM' : 'AM';
  h = h % 12 || 12;
  return `${month}/${day} ${pad(h)}:${m} ${ap}`;
}

function parseDueDate(d) {
  if (!d) return null;
  if (/^d{4}-d{2}-d{2}$/.test(d)) return new Date(d);
  const parts = d.split('/');
  if (parts.length >= 2) {
    const mo = +parts[0], da = +parts[1],
          yr = parts.length === 3 ? +parts[2] : new Date().getFullYear();
    if (!isNaN(mo) && !isNaN(da) && !isNaN(yr)) return new Date(yr,mo-1,da);
  }
  const dt = new Date(d);
  return isNaN(dt) ? null : dt;
}

function addWorkDays(start, days) {
  let d = new Date(start), added = 0;
  while (added < days) {
    d.setDate(d.getDate() + 1);
    if (isWorkday(d)) added++;
  }
  return d;
}

function subWorkDays(start, days) {
  let d = new Date(start), removed = 0;
  while (removed < days) {
    d.setDate(d.getDate() - 1);
    if (isWorkday(d)) removed++;
  }
  return d;
}

function fmtMMDD(d) {
  const dt = new Date(d);
  const mo = String(dt.getMonth()+1).padStart(2,'0');
  const da = String(dt.getDate()).padStart(2,'0');
  return `${mo}/${da}`;
}

function sortQueue(arr) {
  return [...arr].sort((a,b) => {
    const da = parseDueDate(a.due_date), db = parseDueDate(b.due_date);
    if (da && db) return da - db;
    if (da) return -1;
    if (db) return 1;
    return 0;
  });
}

// â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”
// SCHEDULING + LATE (persist embroidery_start)
// â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”

// â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”
// Section 3: Scheduling & Late (using start_date)
// â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”
function scheduleMachineJobs(jobs) {
  let prevEnd = null;

  return jobs.map((job, idx) => {
    // 1) Calculate cutoff for lateness (6 work-days before due)
    const eedDay = subWorkDays(parseDueDate(job.due_date), 6);
    const cutoff = new Date(eedDay);
    cutoff.setHours(WORK_END_HR, WORK_END_MIN, 0, 0);

    // 2) Pick or stamp a start time (use start_date from your sheet)
    let start;
    if (idx === 0) {
      if (job.start_date) {
        // reuse stored start_date
        start = clampToWorkHours(new Date(job.start_date));
      } else {
        // first-time at top: stamp â€œnowâ€ into start_date and persist
        const nowIso = new Date().toISOString();
        job.start_date = nowIso;
        start = clampToWorkHours(new Date(nowIso));

        // **Reformatted axios call below**:
        axios.put(
          `${API_ROOT}/orders/${job.id}`,
          { start_date: nowIso }
        ).catch(console.error);
      }
    } else {
      // chain from previous jobâ€™s end + 30m
      start = clampToWorkHours(new Date(prevEnd.getTime() + 30 * 60000));
    }

    // 3) Calculate run time â†’ end
    const qty   = job.quantity % 6 === 0
      ? job.quantity
      : Math.ceil(job.quantity / 6) * 6;
    const runMs = (job.stitchCount / 30000) * (qty / 6) * 3600000;
    const end   = addWorkTime(start, runMs);

    // 4) Decorate for display
    job._rawStart = start;
    job._rawEnd   = end;
    job.start     = fmtDT(start);
    job.end       = fmtDT(end);
    job.delivery  = fmtMMDD(addWorkDays(end, 6));
    job.isLate    = end > cutoff;

    prevEnd = end;
    return job;
  });
}

// === Section 4: Link Utilities ===
function loadLinks() {
  try {
    return JSON.parse(localStorage.getItem('jobLinks') || '{}');
  } catch {
    return {};
  }
}

function saveLinks(map) {
  localStorage.setItem('jobLinks', JSON.stringify(map));
}

function getChain(jobs, id) {
  const fwd = {}, rev = {};
  jobs.forEach(j => {
    if (j.linkedTo) {
      fwd[j.id]    = j.linkedTo;
      rev[j.linkedTo] = j.id;
    }
  });

  // find root of the chain
  let root = id;
  while (rev[root]) {
    root = rev[root];
  }

  // build the chain array
  const chain = [];
  let cur = root;
  while (cur) {
    chain.push(cur);
    cur = fwd[cur];
  }
  return chain;
}

// === Section 5: FETCH & MERGE (with debug logging) ===
const fetchAll = async () => {
  console.log('â–¶ï¸ fetchAll: starting data load');

  try {
    // a) preserve any manual embroidery_start on existing cards
    const prevEmb = {};
    Object.values(columns)
      .flatMap(col => col.jobs)
      .forEach(j => {
        prevEmb[j.id] = j.embroidery_start || j.start_date || '';
      });
    console.log('  â€“ prevEmb map:', prevEmb);

    // b) fetch both orders and embroideryList in parallel
    const [{ data: orders }, { data: embList }] = await Promise.all([
      axios.get(`${API_ROOT}/orders`),
      axios.get(`${API_ROOT}/embroideryList`),
    ]);
    console.log('  â€“ raw orders:', orders);
    console.log('  â€“ raw embroideryList:', embList);

    // c) build embroidery start map from sheet
    const embMap = {};
    embList.forEach(row => {
      const id = String(row['Order #'] || '').trim();
      if (id) embMap[id] = row['Embroidery Start Time'] || '';
    });
    console.log('  â€“ embMap:', embMap);

    // d) assemble lookup of all jobs by ID
    const jobById = {};
    orders.forEach(o => {
      if (!o.id) return;
      const id = String(o.id).trim();
      const rawTs = embMap[id] ?? prevEmb[id] ?? '';
      const iso   = parseMMDDTimestamp(rawTs) || rawTs || '';
      jobById[id] = {
        ...o,
        id,
        stitchCount:      o.stitch_count,
        quantity:         o.quantity,
        company:          o.company,
        design:           o.design,
        due_date:         o.due_date,
        due_type:         o.due_type,
        embroidery_start: iso,
        start_date:       iso,
        position:         o.position != null ? o.position : null,
        linkedTo:         links[o.id] || null,
        // if your API uses a different field for machine assignment, inspect `o`
        machineId:        o.machineId ?? o.machine ?? '',
      };
    });
    console.log('  â€“ jobById:', jobById);

    // e) rebuild Machine 1 in existing order + append new
    const existing1 = columns.machine1.jobs.map(j => j.id);
    const machine1Jobs = existing1
      .filter(id => jobById[id])
      .map(id => jobById[id])
      .concat(
        Object.values(jobById).filter(
          j => j.machineId === 'machine1' && !existing1.includes(j.id)
        )
      );
    console.log('  â€“ machine1Jobs:', machine1Jobs);

    // f) rebuild Machine 2
    const existing2 = columns.machine2.jobs.map(j => j.id);
    const machine2Jobs = existing2
      .filter(id => jobById[id])
      .map(id => jobById[id])
      .concat(
        Object.values(jobById).filter(
          j => j.machineId === 'machine2' && !existing2.includes(j.id)
        )
      );
    console.log('  â€“ machine2Jobs:', machine2Jobs);

    // g) build Queue: everything not assigned to a machine
    const queueJobs = Object.values(jobById)
      .filter(j => !['machine1', 'machine2'].includes(j.machineId))
      .map(j => ({
        ...j,
        start_date: '',
        _rawStart:  null,
        _rawEnd:    null,
        start:      '',
        end:        '',
        delivery:   '',
        isLate:     false,
      }))
      .sort((a, b) => {
        const da = parseDueDate(a.due_date), db = parseDueDate(b.due_date);
        if (da && db) return da - db;
        if (da) return -1;
        if (db) return 1;
        return 0;
      });
    console.log('  â€“ queueJobs:', queueJobs);

    // h) finally, schedule runtimes and push into state
    const next = {
      queue:    { ...columns.queue,    jobs: queueJobs },
      machine1: { ...columns.machine1, jobs: scheduleMachineJobs(machine1Jobs) },
      machine2: { ...columns.machine2, jobs: scheduleMachineJobs(machine2Jobs) },
    };
    console.log('â–¶ï¸ fetchAll: setting columns â†’', next);
    setColumns(next);

  } catch (err) {
    console.error('âš ï¸ fetchAll error:', err);
  }
};


// === Section 6: Placeholder CRUD ===
const submitPlaceholder = () => {
const job = {
id:         ph.id || `ph-${Date.now()}`,
company:    ph.company,
quantity:   +ph.quantity || 1,
stitchCount:+ph.stitchCount||30000,
due_date:   ph.inHand,
