};

// === Section 7: toggleLink ===
const toggleLink = (colId, idx) => {
  setColumns(cols => {
    // clone this columnâ€™s jobs
    const jobs = [...cols[colId].jobs];
    const job  = jobs[idx];
    const below = jobs[idx + 1];
    if (!below) return cols;  // nothing below to link to

    // update our persisted links map
    setLinks(l => {
      const m = { ...l };
      if (m[job.id] === below.id) delete m[job.id];
      else m[job.id] = below.id;
      return m;
    });

    // toggle the in-memory link flag
    job.linkedTo = job.linkedTo === below.id ? null : below.id;
    jobs[idx] = job;

    // return updated columns state
    return {
      ...cols,
      [colId]: { ...cols[colId], jobs }
    };
  });
};

// === Section 8: onDragEnd ===
const onDragEnd = useCallback((result) => {
  const { source, destination, draggableId } = result;
  if (!destination) return;

  // 1) Clone all three lists
  const lists = {
    queue:    Array.from(columns.queue.jobs),
    machine1: Array.from(columns.machine1.jobs),
    machine2: Array.from(columns.machine2.jobs),
  };

  // 2) Remove dragged chain from source
  const sourceList = lists[source.droppableId];
  const chain      = getChain(sourceList, draggableId);
  const movedSet   = new Set(chain);
  const movedJobs  = sourceList.filter(j => movedSet.has(j.id));
  lists[source.droppableId] = sourceList.filter(j => !movedSet.has(j.id));

  // 3) Insert into destination
  const destList = lists[destination.droppableId];
  destList.splice(destination.index, 0, ...movedJobs);
  lists[destination.droppableId] = destList;

  // 4) Build new columns state
  const newCols = {
    queue:    { ...columns.queue,    jobs: sortQueue(lists.queue) },
    machine1: { ...columns.machine1, jobs: scheduleMachineJobs(lists.machine1) },
    machine2: { ...columns.machine2, jobs: scheduleMachineJobs(lists.machine2) },
  };

  // 5) Apply it
  setColumns(newCols);

  // 6) Persist manual order so refresh wonâ€™t scramble them
  localStorage.setItem(
    'machine1Order',
    JSON.stringify(newCols.machine1.jobs.map(j => j.id))
  );
  localStorage.setItem(
    'machine2Order',
    JSON.stringify(newCols.machine2.jobs.map(j => j.id))
  );
}, [
  columns,
  getChain,
  scheduleMachineJobs,
  sortQueue,
  setColumns
]);
// === Section 9: Render / Return ===
return (
  <div style={{ padding: 16, fontFamily: 'sans-serif', fontSize: 13 }}>

    {/* Add / Sync Buttons */}
    <button
      onClick={() => setShowModal(true)}
      style={{ marginRight: 8, fontSize: 13 }}
    >
      + Add Placeholder
    </button>
    <button onClick={handleSync} style={{ fontSize: 13 }}>
      Sync from Sheet{' '}
      {syncStatus === 'updated' && (
        <span style={{ color: 'green', fontSize: 13 }}>âœ“ Updated</span>
      )}
    </button>

    {/* Legend */}
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        gap: 16,
        margin: '12px 0',
        flexWrap: 'wrap',
        fontSize: 12,
      }}
    >
      {[
        { label: 'Placeholder', bg: LIGHT_YELLOW, border: DARK_YELLOW },
        { label: 'Soft Date',    bg: LIGHT_GREY,   border: DARK_GREY   },
        { label: 'Hard Date',    bg: LIGHT_PURPLE, border: DARK_PURPLE },
        { label: 'Late',         bg: 'red',        border: 'red'       },
      ].map((item, i) => (
        <div key={i} style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
          <span
            style={{
              width: 12,
              height: 12,
              background: item.bg,
              border: `2px solid ${item.border}`,
              borderRadius: 2,
            }}
          />
          {item.label}
        </div>
      ))}
      <div style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
        <span style={{ fontSize: 14 }}>ðŸ”—</span> Link
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
        <span style={{ fontSize: 14 }}>âŒ</span> Unlink
      </div>
      {['Start', 'EED / End', 'IHD / Delivery'].map((txt, i) => {
        const color = i === 0 ? BUBBLE_START : i === 1 ? BUBBLE_END : BUBBLE_DELIV;
        return (
          <div key={i} style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
            <span
              style={{
                width: 12,
                height: 12,
                background: color,
                borderRadius: 2,
              }}
            />
            {txt}
          </div>
        );
      })}
    </div>

    {/* Drag & Drop Columns */}
    <DragDropContext onDragEnd={onDragEnd}>
      <div style={{ display: 'flex', gap: 16, marginTop: 16 }}>
        {['queue', 'machine1', 'machine2'].map(colId => {
          const col = columns[colId] || {};
          const jobs = Array.isArray(col.jobs) ? col.jobs : [];
          // build segments of linked chains
          const segments = [];
          let i = 0;
          while (i < jobs.length) {
            const chain = getChain(jobs, jobs[i].id);
            const len = chain.length > 1 ? chain.length : 1;
            segments.push({ start: i, len });
            i += len;
          }
          const GAP = 6, OUTLINE = 6;

          return (
            <Droppable key={colId} droppableId={colId}>
              {provided => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    position: 'relative',
                    border: '1px solid #ccc',
                    borderRadius: 4,
                    width: colId === 'queue' ? 260 : 300,
                    minHeight: 400,
                    padding: 12,
                    background: '#fafafa',
                  }}
                >
                  <h4 style={{ textAlign: 'center', margin: '8px 0', fontSize: 13 }}>
                    {col.title}
                  </h4>

                  {segments.map((seg, si) => (
                    <div
                      key={si}
                      style={{
                        position: 'relative',
                        marginBottom: GAP,
                        zIndex: seg.len > 1 ? 3 : 1,
                        outline: seg.len > 1 ? `${OUTLINE}px solid #0288d1` : undefined,
                        outlineOffset: seg.len > 1 ? '0px' : undefined,
                        borderRadius: 4,
                      }}
                    >
                      {jobs.slice(seg.start, seg.start + seg.len).map((job, jIdx) => {
                        const idx = seg.start + jIdx;
                        const isPh = job.id.startsWith('ph-');
                        const isSoft = job.due_type === 'Soft Date';
                        const base = isPh
                          ? LIGHT_YELLOW
                          : isSoft
                          ? LIGHT_GREY
                          : LIGHT_PURPLE;
                        let bg = base;
                        let bCol = isPh
                          ? DARK_YELLOW
                          : isSoft
                          ? DARK_GREY
                          : DARK_PURPLE;
                        if (job.isLate) {
                          bg = `repeating-linear-gradient(
                            45deg,
                            ${base}, ${base} 6px,
                            rgba(255,0,0,0.5) 6px,
                            rgba(255,0,0,0.5) 12px
                          )`;
                          bCol = 'red';
                        }

                        return (
                          <Draggable key={job.id} draggableId={job.id} index={idx}>
                            {prov => (
                              <div
                                ref={prov.innerRef}
                                {...prov.draggableProps}
                                {...prov.dragHandleProps}
                                style={{
                                  position: 'relative',
                                  display: 'grid',
                                  gridTemplateColumns: '1fr auto',
                                  gridTemplateRows: 'auto auto auto auto',
                                  columnGap: 6,
                                  rowGap: 4,
                                  padding: '6px 28px 6px 6px',
                                  margin: `0 0 ${jIdx < seg.len - 1 ? GAP : 0}px 0`,
                                  background: bg,
                                  border: `2px solid ${bCol}`,
                                  borderRadius: 4,
                                  zIndex: 2,
                                  ...prov.draggableProps.style,
                                }}
                              >
                                {jIdx === 0 && seg.len > 1 && (
                                  <div
                                    style={{
                                      position: 'absolute',
                                      top: 0,
                                      right: 0,
                                      width: 4,
                                      height: '100%',
                                      background: '#0288d1',
                                      zIndex: 4,
                                    }}
                                  />
                                )}

                                {/* Row1 Col1: ID + Company */}
                                <span
                                  style={{
                                    gridRow: 1,
                                    gridColumn: 1,
                                    background: base,
                                    padding: '0 4px',
                                    borderRadius: 4,
                                    whiteSpace: 'nowrap',
                                    overflow: 'hidden',
                                    textOverflow: 'ellipsis',
                                    fontSize: 13,
                                    fontWeight: 'bold',
                                  }}
                                >
                                  <span
                                    style={{
                                      display: 'inline-block',
                                      width: 20,
                                      height: 20,
                                      borderRadius: '50%',
                                      background: '#000',
                                      color: base,
                                      lineHeight: '20px',
                                      textAlign: 'center',
                                      fontSize: isPh ? 13 : 11,
                                      fontWeight: 'bold',
                                      marginRight: 4,
                                    }}
                                  >
                                    {isPh ? '*' : job.id}
                                  </span>
                                  {job.company}
                                </span>

                                {/* Row1 Col2: Quantity */}
                                <span
                                  style={{
                                    gridRow: 1,
                                    gridColumn: 2,
                                    justifySelf: 'end',
                                    background: base,
                                    padding: '0 4px',
                                    borderRadius: 4,
                                    fontSize: 15,
                                    fontWeight: 'bold',
                                    whiteSpace: 'nowrap',
                                    overflow: 'hidden',
                                    textOverflow: 'ellipsis',
                                  }}
                                >
                                  {job.quantity}
                                </span>

                                {/* Row2 Col1: Design */}
                                <span
                                  style={{
                                    gridRow: 2,
                                    gridColumn: 1,
                                    background: base,
                                    padding: '0 4px',
                                    borderRadius: 4,
                                    whiteSpace: 'nowrap',
                                    overflow: 'hidden',
                                    textOverflow: 'ellipsis',
                                    fontSize: 13,
                                  }}
                                >
                                  {job.design?.slice(0, 22)}
                                </span>

                                {/* Row2 Col2: Start */}
                                {job.start && (
                                  <span
                                    style={{
                                      gridRow: 2,
                                      gridColumn: 2,
                                      justifySelf: 'end',
                                      background: BUBBLE_START,
                                      padding: '0 4px',
                                      borderRadius: 10,
                                      whiteSpace: 'nowrap',
                                      fontSize: 13,
                                    }}
                                  >
                                    {job.start}
                                  </span>
                                )}

                                {/* Row3 Col1: EED */}
                                <span
                                  style={{
                                    gridRow: 3,
                                    gridColumn: 1,
                                    background: BUBBLE_END,
                                    padding: '0 4px',
                                    borderRadius: 4,
                                    whiteSpace: 'nowrap',
                                    overflow: 'hidden',
                                    textOverflow: 'ellipsis',
                                    fontSize: 13,
                                  }}
                                >
                                  {`EED: ${fmtMMDD(subWorkDays(parseDueDate(job.due_date), 6))}`}
                                </span>

                                {/* Row3 Col2: End */}
                                {job.end && (
                                  <span
                                    style={{
                                      gridRow: 3,
                                      gridColumn: 2,
                                      justifySelf: 'end',
                                      background: BUBBLE_END,
                                      padding: '0 4px',
                                      borderRadius: 10,
                                      whiteSpace: 'nowrap',
                                      fontSize: 13,
                                    }}
                                  >
                                    {job.end}
                                  </span>
                                )}

                                {/* Row4 Col1: IHD */}
                                <span
                                  style={{
                                    gridRow: 4,
                                    gridColumn: 1,
                                    background: BUBBLE_DELIV,
                                    padding: '0 4px',
                                    borderRadius: 4,
                                    whiteSpace: 'nowrap',
                                    fontSize: 13,
                                  }}
                                >
                                  {`IHD: ${fmtMMDD(job.due_date)}`}
                                </span>

                                {/* Row4 Col2: Delivery */}
                                {job.delivery && (
                                  <span
                                    style={{
                                      gridRow: 4,
                                      gridColumn: 2,
                                      justifySelf: 'end',
                                      background: BUBBLE_DELIV,
                                      padding: '0 4px',
                                      borderRadius: 10,
                                      whiteSpace: 'nowrap',
                                      fontSize: 13,
                                    }}
                                  >
                                    {job.delivery}
                                  </span>
                                )}

                                {/* Top-right tab: Due-Type */}
                                <div
                                  style={{
                                    position: 'absolute',
                                    top: 0,
                                    right: 0,
                                    width: 28,
                                    height: 28,
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    background: base,
                                    borderTopRightRadius: 4,
                                    borderBottomLeftRadius: 4,
                                    zIndex: 4,
                                    fontSize: 11,
                                    fontWeight: 'bold',
                                  }}
                                >
                                  {(job.due_type || '')[0] || ''}
                                </div>

                                {/* Link / Unlink toggle */}
                                {jIdx < seg.len - 1 && (
                                  <div
                                    onClick={() => toggleLink(colId, idx)}
                                    style={{
                                      position: 'absolute',
                                      top: 28,
                                      right: 0,
                                      width: 28,
                                      height: `calc(100% - 28px)`,
                                      display: 'flex',
                                      flexDirection: 'column',
                                      alignItems: 'center',
                                      justifyContent: 'center',
                                      cursor: 'pointer',
                                      background: base,
                                      borderBottomRightRadius: 4,
                                      zIndex: 4,
                                    }}
                                  >
                                    {job.linkedTo === jobs[idx + 1]?.id ? 'âŒ' : 'ðŸ”—'}
                                  </div>
                                )}

                                {/* Placeholder edit/delete */}
                                {isPh && (
                                  <div
                                    style={{
                                      position: 'absolute',
                                      top: 0,
                                      right: 0,
                                      width: 28,
                                      height: '100%',
                                      display: 'flex',
                                      flexDirection: 'column',
                                      alignItems: 'center',
                                      justifyContent: 'flex-start',
                                      background: base,
                                      borderTopRightRadius: 4,
                                      borderBottomRightRadius: 4,
                                      zIndex: 4,
                                    }}
                                  >
                                    <span
                                      onClick={() => editPlaceholder(job)}
                                      style={{ cursor: 'pointer', fontSize: 12, margin: 4 }}
                                    >
                                      âœŽ
                                    </span>
                                    <span
                                      onClick={() => removePlaceholder(job.id)}
                                      style={{ cursor: 'pointer', fontSize: 12, margin: 4 }}
                                    >
                                      âœ–
                                    </span>
                                  </div>
                                )}
                              </div>
                            )}
                          </Draggable>
                        );
                      })}
                    </div>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          );
        })}
      </div>
    </DragDropContext>
  </div>
);
}
