// File: frontend/src/Section9.jsx
import React from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { fmtMMDD, subWorkDays, parseDueDate } from './helpers';

export default function Section9({
  columns,
  handleSync,
  syncStatus,
  showModal,
  setShowModal,
  onDragEnd,
  getChain,
  toggleLink,
  editPlaceholder,
  removePlaceholder,
  ph,
  setPh,
  submitPlaceholder,
  LIGHT_YELLOW,
  DARK_YELLOW,
  LIGHT_GREY,
  DARK_GREY,
  LIGHT_PURPLE,
  DARK_PURPLE,
  BUBBLE_START,
  BUBBLE_END,
  BUBBLE_DELIV
}) {
  return (
    <div style={{ padding: 16, fontFamily: 'sans-serif', fontSize: 13 }}>

      {/* Add / Sync Buttons */}
      <button onClick={() => setShowModal(true)} style={{ marginRight: 8, fontSize: 13 }}>
        + Add Placeholder
      </button>
      <button onClick={handleSync} style={{ fontSize: 13 }}>
        Sync from Sheet{' '}
        {syncStatus === 'updated' && <span style={{ color: 'green' }}>‚úì Updated</span>}
      </button>

      {/* Legend */}
      <div style={{ display: 'flex', alignItems: 'center', gap: 16, margin: '12px 0', flexWrap: 'wrap', fontSize: 12 }}>
        {[ 
          { label: 'Placeholder', bg: LIGHT_YELLOW, border: DARK_YELLOW },
          { label: 'Soft Date',    bg: LIGHT_GREY,   border: DARK_GREY   },
          { label: 'Hard Date',    bg: LIGHT_PURPLE, border: DARK_PURPLE },
          { label: 'Late',         bg: 'red',        border: 'red'       },
        ].map((item,i) => (
          <div key={i} style={{ display:'flex', alignItems:'center', gap:4 }}>
            <span style={{ width:12, height:12, background:item.bg, border:`2px solid ${item.border}`, borderRadius:2 }}/>
            {item.label}
          </div>
        ))}
        <div style={{ display:'flex',alignItems:'center',gap:4 }}><span>üîó</span>Link</div>
        <div style={{ display:'flex',alignItems:'center',gap:4 }}><span>‚ùå</span>Unlink</div>
        {['Start','EED / End','IHD / Delivery'].map((txt,i)=>{
          const color = i===0?BUBBLE_START:i===1?BUBBLE_END:BUBBLE_DELIV;
          return (
            <div key={i} style={{ display:'flex',alignItems:'center',gap:4 }}>
              <span style={{ width:12, height:12, background:color, borderRadius:2 }}/>
              {txt}
            </div>
          );
        })}
      </div>

      {/* Drag & Drop Columns */}
      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display:'flex', gap:16, marginTop:16 }}>
          {['queue','machine1','machine2'].map(colId => {
            const col = columns[colId] || {};
            const jobs = Array.isArray(col.jobs)?col.jobs:[];

            // break into linked‚Äêsegments
            const segments = [];
            let idx=0;
            while(idx<jobs.length){
              const chain = getChain(jobs,jobs[idx].id);
              const len = chain.length>1?chain.length:1;
              segments.push({ start:idx, len });
              idx += len;
            }

            return (
              <Droppable key={colId} droppableId={colId}>
                {provided=>(
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    style={{
                      position:'relative',
                      border:'1px solid #ccc',
                      borderRadius:4,
                      width:colId==='queue'?260:300,
                      minHeight:400,
                      padding:12,
                      background:'#fafafa'
                    }}
                  >
                    <h4 style={{ textAlign:'center', margin:'8px 0', fontSize:13 }}>
                      {col.title}
                    </h4>

                    {segments.map((seg,sIdx)=>(
                      <div key={sIdx} style={{
                        position:'relative',
                        marginBottom:6,
                        zIndex: seg.len>1?3:1,
                        outline: seg.len>1?'6px solid #0288d1':undefined,
                        outlineOffset: seg.len>1?'0px':undefined,
                        borderRadius:4
                      }}>
                        {jobs.slice(seg.start,seg.start+seg.len).map((job,jIdx)=>{
                          const globalIdx = seg.start + jIdx;
                          const isPh   = job.id.startsWith('ph-');
                          const isSoft = job.due_type==='Soft Date';
                          const base   = isPh?LIGHT_YELLOW: isSoft?LIGHT_GREY:LIGHT_PURPLE;
                          let bg   = base, bCol = isPh?DARK_YELLOW: isSoft?DARK_GREY:DARK_PURPLE;
                          if(job.isLate){ bg=`repeating-linear-gradient(45deg, ${base}, ${base} 6px, rgba(255,0,0,0.5) 6px, rgba(255,0,0,0.5) 12px)`; bCol='red'; }

                          return (
                            <Draggable key={job.id} draggableId={job.id} index={globalIdx}>
                              {prov=>(
                                <div
                                  ref={prov.innerRef}
                                  {...prov.draggableProps}
                                  {...prov.dragHandleProps}
                                  style={{
                                    position:'relative',
                                    display:'grid',
                                    gridTemplateColumns:'1fr auto',
                                    gridTemplateRows:'auto auto auto auto',
                                    columnGap:6,
                                    rowGap:4,
                                    padding:'6px 28px 6px 6px',
                                    margin:`0 0 ${jIdx<seg.len-1?6:0}px 0`,
                                    background:bg,
                                    border:`2px solid ${bCol}`,
                                    borderRadius:4,
                                    zIndex:2,
                                    ...prov.draggableProps.style
                                  }}
                                >
                                  {/* Row1: ID + Qty */}
                                  <span style={{ gridRow:1,gridColumn:1, background:base,padding:'0 4px',borderRadius:4,fontSize:13,fontWeight:'bold',whiteSpace:'nowrap',overflow:'hidden',textOverflow:'ellipsis' }}>
                                    <span style={{ display:'inline-block',width:20,height:20,borderRadius:'50%',background:'#000',color:base,lineHeight:'20px',textAlign:'center',fontSize:isPh?13:11,fontWeight:'bold',marginRight:4 }}>
                                      {isPh?'*':job.id}
                                    </span>
                                    {job.company}
                                  </span>
                                  <span style={{ gridRow:1,gridColumn:2,justifySelf:'end', background:base,padding:'0 4px',borderRadius:4,fontSize:15,fontWeight:'bold',whiteSpace:'nowrap',overflow:'hidden',textOverflow:'ellipsis' }}>
                                    {job.quantity}
                                  </span>

                                  {/* Row2: design + start bubble */}
                                  <span style={{ gridRow:2,gridColumn:1, background:base,padding:'0 4px',borderRadius:4,fontSize:13,whiteSpace:'nowrap',overflow:'hidden',textOverflow:'ellipsis' }}>
                                    {job.design?.slice(0,22)}
                                  </span>
                                  {job.start && (
                                    <span style={{ gridRow:2,gridColumn:2,justifySelf:'end',background:BUBBLE_START,padding:'0 4px',borderRadius:10,fontSize:13,whiteSpace:'nowrap' }}>
                                      {job.start}
                                    </span>
                                  )}

                                  {/* Row3: EED + end */}
                                  <span style={{ gridRow:3,gridColumn:1, background:BUBBLE_END,padding:'0 4px',borderRadius:4,fontSize:13,whiteSpace:'nowrap',overflow:'hidden',textOverflow:'ellipsis' }}>
                                    {`EED: ${fmtMMDD(subWorkDays(parseDueDate(job.due_date),6))}`}
                                  </span>
                                  {job.end && (
                                    <span style={{ gridRow:3,gridColumn:2,justifySelf:'end',background:BUBBLE_END,padding:'0 4px',borderRadius:10,fontSize:13,whiteSpace:'nowrap' }}>
                                      {job.end}
                                    </span>
                                  )}

                                  {/* Row4: IHD + delivery */}
                                  <span style={{ gridRow:4,gridColumn:1, background:BUBBLE_DELIV,padding:'0 4px',borderRadius:4,fontSize:13,whiteSpace:'nowrap' }}>
                                    {`IHD: ${fmtMMDD(job.due_date)}`}
                                  </span>
                                  {job.delivery && (
                                    <span style={{ gridRow:4,gridColumn:2,justifySelf:'end',background:BUBBLE_DELIV,padding:'0 4px',borderRadius:10,fontSize:13,whiteSpace:'nowrap' }}>
                                      {job.delivery}
                                    </span>
                                  )}

                                  {/* Due-Type tab */}
                                  <div style={{
                                    position:'absolute',top:0,right:0,width:28,height:28,display:'flex',alignItems:'center',justifyContent:'center',
                                    background:base,borderTopRightRadius:4,borderBottomLeftRadius:4,zIndex:4,fontSize:11,fontWeight:'bold'
                                  }}>
                                    {(job.due_type||'')[0]||''}
                                  </div>

                                  {/* Link / Unlink toggle tab */}
                                  {jIdx < seg.len-1 && (
                                    <div
                                      onClick={()=>toggleLink(colId,globalIdx)}
                                      style={{
                                        position:'absolute',top:0,right:28,width:28,height:28,display:'flex',alignItems:'center',justifyContent:'center',
                                        cursor:'pointer',background:base,borderTopLeftRadius:4,borderBottomLeftRadius:4,zIndex:5
                                      }}
                                    >
                                      {job.linkedTo === jobs[globalIdx+1]?.id ? '‚ùå' : 'üîó'}
                                    </div>
                                  )}

                                  {/* Edit/Delete for placeholders */}
                                  {isPh && (
                                    <div style={{
                                      position:'absolute',top:0,right:56,width:28,height:'100%',display:'flex',flexDirection:'column',alignItems:'center',justifyContent:'flex-start',
                                      background:base,borderTopRightRadius:4,borderBottomRightRadius:4,zIndex:4
                                    }}>
                                      <span onClick={()=>editPlaceholder(job)} style={{ cursor:'pointer',fontSize:12,margin:4 }}>‚úé</span>
                                      <span onClick={()=>removePlaceholder(job.id)} style={{ cursor:'pointer',fontSize:12,margin:4 }}>‚úñ</span>
                                    </div>
                                  )}
                                </div>
                              )}
                            </Draggable>
                          );
                        })}
                      </div>
                    ))}

                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            );
          })}
        </div>
      </DragDropContext>

      {/* Placeholder Modal */}
      {showModal && (
        <div style={{
          position:'fixed',top:0,left:0,width:'100vw',height:'100vh',background:'rgba(0,0,0,0.5)',
          display:'flex',alignItems:'center',justifyContent:'center',zIndex:1000
        }}>
          <div style={{
            background:'#fff',padding:24,borderRadius:8,width:320,boxShadow:'0 2px 10px rgba(0,0,0,0.2)'
          }}>
            <h3 style={{ marginTop:0 }}>Add / Edit Placeholder</h3>
            <div style={{ display:'flex',flexDirection:'column',gap:12 }}>
              <input type="text" placeholder="Company" value={ph.company}
                onChange={e=>setPh(p=>({...p,company:e.target.value}))}
                style={{ padding:8,fontSize:13,width:'100%' }}
              />
              <input type="number" placeholder="Quantity" value={ph.quantity}
                onChange={e=>setPh(p=>({...p,quantity:e.target.value}))}
                style={{ padding:8,fontSize:13,width:'100%' }}
              />
              <input type="number" placeholder="Stitch Count" value={ph.stitchCount}
                onChange={e=>setPh(p=>({...p,stitchCount:e.target.value}))}
                style={{ padding:8,fontSize:13,width:'100%' }}
              />
              <input type="date" placeholder="In Hand Date" value={ph.inHand}
                onChange={e=>setPh(p=>({...p,inHand:e.target.value}))}
                style={{ padding:8,fontSize:13,width:'100%' }}
              />
              <select value={ph.dueType} onChange={e=>setPh(p=>({...p,dueType:e.target.value}))}
                style={{ padding:8,fontSize:13,width:'100%' }}
              >
                <option>Hard Date</option>
                <option>Soft Date</option>
              </select>
            </div>
            <div style={{ marginTop:16,textAlign:'right' }}>
              <button onClick={()=>setShowModal(false)} style={{ marginRight:8,fontSize:13 }}>Cancel</button>
              <button onClick={submitPlaceholder} style={{ fontSize:13 }}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
