// === Section 1: Imports & Configuration ===
// File: frontend/src/App.js

import React, { useEffect, useState, useCallback } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';

// CONFIGURATION
const API_ROOT       = 'http://localhost:5000/api';
const WORK_START_HR  = 8,  WORK_START_MIN = 30;
const WORK_END_HR    = 16, WORK_END_MIN   = 30;
const WEEKENDS       = [0,6]; // Sunday=0, Saturday=6
const HOLIDAYS       = ['2025-01-01','2025-12-25'];

// COLOR CONSTANTS
const LIGHT_YELLOW  = '#FFF9C4', DARK_YELLOW  = '#FDD835';
const LIGHT_GREY    = '#ECEFF1', DARK_GREY    = '#616161';
const LIGHT_PURPLE  = '#E1BEE7', DARK_PURPLE  = '#6A1B9A';
const BUBBLE_START  = '#e0f7fa';
const BUBBLE_END    = '#ffe0b2';
const BUBBLE_DELIV  = '#c8e6c9';

// ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî
// HELPERS (unchanged)
// ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî

// === Section 2: Helpers ===
function isHoliday(dt) {
  return dt instanceof Date &&
         !isNaN(dt) &&
         HOLIDAYS.includes(dt.toISOString().slice(0,10));
}
function isWorkday(dt) {
  return dt instanceof Date &&
         !isNaN(dt) &&
         !WEEKENDS.includes(dt.getDay()) &&
         !isHoliday(dt);
}
function clampToWorkHours(dt) {
  let d = new Date(dt);
  while (
    !isWorkday(d) ||
    d.getHours() < WORK_START_HR ||
    (d.getHours() === WORK_START_HR && d.getMinutes() < WORK_START_MIN)
  ) {
    d.setDate(d.getDate() + 1);
    d.setHours(WORK_START_HR, WORK_START_MIN, 0, 0);
  }
  if (
    d.getHours() > WORK_END_HR ||
    (d.getHours() === WORK_END_HR && d.getMinutes() >= WORK_END_MIN)
  ) {
    d.setDate(d.getDate() + 1);
    d.setHours(WORK_START_HR, WORK_START_MIN, 0, 0);
    return clampToWorkHours(d);
  }
  return d;
}
function addWorkTime(start, ms) {
  let remaining = ms;
  let current = clampToWorkHours(start);
  while (remaining > 0) {
    const endOfDay = new Date(current);
    endOfDay.setHours(WORK_END_HR, WORK_END_MIN, 0, 0);
    const free = endOfDay.getTime() - current.getTime();
    if (free <= 0) {
      current = clampToWorkHours(new Date(current.setDate(current.getDate() + 1)));
    } else if (remaining <= free) {
      current = new Date(current.getTime() + remaining);
      remaining = 0;
    } else {
      remaining -= free;
      current = new Date(endOfDay);
    }
  }
  return current;
}
function fmtDT(dt) {
  const pad = n => String(n).padStart(2,'0');
  const month = pad(dt.getMonth() + 1);
  const day   = pad(dt.getDate());
  let h = dt.getHours(), m = pad(dt.getMinutes()), ap = h >= 12 ? 'PM' : 'AM';
  h = h % 12 || 12;
  return `${month}/${day} ${pad(h)}:${m} ${ap}`;
}
function parseDueDate(d) {
  if (!d) return null;
  if (/^\d{4}-\d{2}-\d{2}$/.test(d)) return new Date(d);
  const parts = d.split('/');
  if (parts.length >= 2) {
    const mo = +parts[0], da = +parts[1],
          yr = parts.length === 3 ? +parts[2] : new Date().getFullYear();
    if (!isNaN(mo)&&!isNaN(da)&&!isNaN(yr)) return new Date(yr,mo-1,da);
  }
  const dt = new Date(d);
  return isNaN(dt) ? null : dt;
}
function addWorkDays(start, days) {
  let d = new Date(start), added = 0;
  while (added < days) {
    d.setDate(d.getDate() + 1);
    if (isWorkday(d)) added++;
  }
  return d;
}
function subWorkDays(start, days) {
  let d = new Date(start), removed = 0;
  while (removed < days) {
    d.setDate(d.getDate() - 1);
    if (isWorkday(d)) removed++;
  }
  return d;
}
function fmtMMDD(d) {
  const dt = new Date(d);
  const mo = String(dt.getMonth()+1).padStart(2,'0');
  const da = String(dt.getDate()).padStart(2,'0');
  return `${mo}/${da}`;
}
function sortQueue(arr) {
  return [...arr].sort((a,b) => {
    const da = parseDueDate(a.due_date), db = parseDueDate(b.due_date);
    if (da && db) return da - db;
    if (da) return -1;
    if (db) return 1;
    return 0;
  });
}

// ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî
// SCHEDULING + LATE (unchanged)
// ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî

// === Section 3: Scheduling & Late ===
function scheduleMachineJobs(jobs) {
  let prevEnd = null;
  return jobs.map((job, idx) => {
    let start;
    if (idx === 0) {
      start = job.embroidery_start
        ? clampToWorkHours(new Date(job.embroidery_start))
        : clampToWorkHours(new Date());
      if (!job.embroidery_start) job.embroidery_start = new Date().toISOString();
    } else {
      start = clampToWorkHours(new Date(prevEnd.getTime() + 30*60000));
    }
    const qty   = job.quantity % 6 === 0 ? job.quantity : Math.ceil(job.quantity/6)*6;
    const runMs = (job.stitchCount/30000) * (qty/6) * 3600000;
    const end   = addWorkTime(start, runMs);

    const eedDay = subWorkDays(parseDueDate(job.due_date), 6);
    const cutoff = new Date(eedDay);
    cutoff.setHours(WORK_END_HR, WORK_END_MIN, 0, 0);

    job._rawStart = start;
    job._rawEnd   = end;
    job.start     = fmtDT(start);
    job.end       = fmtDT(end);
    job.delivery  = fmtMMDD(addWorkDays(end, 6));
    job.isLate    = end > cutoff;

    prevEnd = end;
    return job;
  });
}

// ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî
// LINK UTILITIES (unchanged)
// ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî

// === Section 4: Link Utilities ===
function loadLinks() {
  try {
    return JSON.parse(localStorage.getItem('jobLinks')||'{}');
  } catch {
    return {};
  }
}
function saveLinks(map) {
  localStorage.setItem('jobLinks', JSON.stringify(map));
}
function getChain(jobs, id) {
  const fwd = {}, rev = {};
  jobs.forEach(j => {
    if (j.linkedTo) {
      fwd[j.id]       = j.linkedTo;
      rev[j.linkedTo] = j.id;
    }
  });
  let root = id;
  while (rev[root]) root = rev[root];
  const chain = [];
  let cur = root;
  while (cur) {
    chain.push(cur);
    cur = fwd[cur];
  }
  return chain;
}

// ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî
// MAIN COMPONENT
// ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî

// === Section 5: Component & State Hooks ===
export default function App() {
  const [columns, setColumns] = useState({
    queue:    { id: 'queue',    title: 'Unscheduled', jobs: [] },
    machine1: { id: 'machine1', title: 'Machine 1',   jobs: [] },
    machine2: { id: 'machine2', title: 'Machine 2',   jobs: [] },
  });

  const [placeholders, setPlaceholders] = useState(() =>
    JSON.parse(localStorage.getItem('placeholders') || '[]')
  );
  useEffect(() => {
    localStorage.setItem('placeholders', JSON.stringify(placeholders));
  }, [placeholders]);

  const [links, setLinks] = useState(loadLinks);
  useEffect(() => { saveLinks(links); }, [links]);

  const [showModal, setShowModal] = useState(false);
  const [ph, setPh] = useState({
    id: null,
    company: '',
    quantity: '',
    stitchCount: '',
    inHand: '',
    dueType: 'Hard Date'
  });

  const [syncStatus, setSyncStatus] = useState('');

  // FETCH & MERGE (queue always date-sorted; machines manual position)
  const fetchAll = async () => {
    try {
      const { data } = await axios.get(`${API_ROOT}/orders`);
      const real = { queue: [], machine1: [], machine2: [] };

      data.forEach(o => {
        if (!o.title) return;
        const col = (o.machineId === 'machine1' || o.machineId === 'machine2')
          ? o.machineId : 'queue';
        real[col].push({
          ...o,
          id:               o.id.toString(),
          stitchCount:      o.stitch_count,
          quantity:         o.quantity,
          company:          o.company,
          design:           o.design,
          due_date:         o.due_date,
          due_type:         o.due_type,
          start:            o.start_date || '',
          end:              o.end_date   || '',
          delivery:         o.delivery   || '',
          position:         o.position != null ? o.position : null,
          embroidery_start: o.embroidery_start || '',
          linkedTo:         links[o.id] || null
        });
      });

      // sort real machines by stored position
      ['machine1','machine2'].forEach(c => {
        real[c].sort((a,b) => (a.position ?? Infinity) - (b.position ?? Infinity));
      });

      // merge placeholders + real
      const merged = {};
      ['queue','machine1','machine2'].forEach(col => {
        merged[col] = [
          ...placeholders.filter(p => p.machineId === col),
          ...real[col]
        ];
      });

      // set state
      setColumns({
        queue: {
          ...columns.queue,
          jobs: sortQueue(merged.queue)
        },
        machine1: {
          ...columns.machine1,
          jobs: scheduleMachineJobs(merged.machine1)
        },
        machine2: {
          ...columns.machine2,
          jobs: scheduleMachineJobs(merged.machine2)
        }
      });
    } catch (err) {
      console.error('fetchAll error', err);
    }
  };

  // on mount only
  useEffect(() => {
    fetchAll().catch(console.error);
  }, []);

  const handleSync = () => {
    fetchAll().catch(console.error);
    setSyncStatus('updated');
    setTimeout(() => setSyncStatus(''), 2000);
  };

// === Section 6: Placeholder CRUD ===
const submitPlaceholder = () => {
  const job = {
    id:         ph.id || `ph-${Date.now()}`,
    company:    ph.company,
    quantity:   +ph.quantity || 1,
    stitchCount:+ph.stitchCount||30000,
    due_date:   ph.inHand,
    due_type:   ph.dueType,
    machineId:  'queue',
    position:   null
  };
  if (ph.id) {
    setPlaceholders(ps => ps.map(p => p.id===ph.id ? job : p));
    setColumns(c => ({
      ...c,
      queue:    {...c.queue,    jobs: c.queue.jobs.map(j=>j.id===ph.id?job:j)},
      machine1: {...c.machine1, jobs: c.machine1.jobs.map(j=>j.id===ph.id?job:j)},
      machine2: {...c.machine2, jobs: c.machine2.jobs.map(j=>j.id===ph.id?job:j)},
    }));
  } else {
    setPlaceholders(ps => [job, ...ps]);
    setColumns(c => ({
      ...c,
      queue: { ...c.queue, jobs: [job, ...c.queue.jobs] }
    }));
  }
  setShowModal(false);
  setPh({ id:null, company:'', quantity:'', stitchCount:'', inHand:'', dueType:'Hard Date' });
};

const removePlaceholder = id => {
  setPlaceholders(ps => ps.filter(p=>p.id!==id));
  setColumns(c => ({
    ...c,
    queue:    {...c.queue,    jobs: c.queue.jobs.filter(j=>j.id!==id)},
    machine1: {...c.machine1, jobs: c.machine1.jobs.filter(j=>j.id!==id)},
    machine2: {...c.machine2, jobs: c.machine2.jobs.filter(j=>j.id!==id)},
  }));
};

const editPlaceholder = job => {
  setPh({
    id: job.id,
    company: job.company,
    quantity: job.quantity,
    stitchCount: job.stitchCount,
    inHand: job.due_date,
    dueType: job.due_type
  });
  setShowModal(true);
};


// === Section 7: toggleLink ===
const toggleLink = (colId, idx) => {
  setColumns(cols => {
    const jobs = [...cols[colId].jobs];
    const job  = jobs[idx];
    const below = jobs[idx+1];
    if (!below) return cols;

    setLinks(l => {
      const m = { ...l };
      if (m[job.id] === below.id) delete m[job.id];
      else m[job.id] = below.id;
      return m;
    });

    job.linkedTo = job.linkedTo===below.id ? null : below.id;
    jobs[idx] = job;

    return {
      ...cols,
      [colId]: { ...cols[colId], jobs }
    };
  });
};

// === Section 8: onDragEnd ===
const onDragEnd = result => {
  const { source, destination, draggableId } = result;
  if (!destination) return;

  const sc = source.droppableId;
  const dc = destination.droppableId;

  // same-list reorder
  if (sc === dc) {
    const jobs = Array.from(columns[sc].jobs);
    const [moved] = jobs.splice(source.index,1);
    jobs.splice(destination.index,0,moved);
    return setColumns(prev => ({
      ...prev,
      [sc]: { ...prev[sc], jobs }
    }));
  }

  // cross-list
  const sourceJobs = Array.from(columns[sc].jobs);
  const destJobs   = Array.from(columns[dc].jobs);

  // handle dropping between a linked pair
  const before = destJobs[destination.index-1];
  const after  = destJobs[destination.index];
  if (before && after && before.linkedTo===after.id && draggableId!==before.id) {
    setLinks(l => { const m={...l}; delete m[before.id]; return m; });
    before.linkedTo = null;
  }

  // build chain
  const chain = getChain(sourceJobs, draggableId);
  const newSrc = sourceJobs.filter(j=>!chain.includes(j.id));
  const newDst = Array.from(destJobs);
  newDst.splice(destination.index,0,...sourceJobs.filter(j=>chain.includes(j.id)));

  // if back to queue ‚áí unlink & clear timing
  if (dc === 'queue') {
    setLinks(l => { const m={...l}; chain.forEach(id=>delete m[id]); return m; });
    newDst.forEach(job => {
      if (chain.includes(job.id)) {
        job.linkedTo = null;
        job._rawStart = job._rawEnd = null;
        job.start = job.end = job.delivery = '';
        job.isLate = false;
        job.embroidery_start = '';
        job.position = null;
      }
    });
  }

  // update placeholders
  setPlaceholders(ps =>
    ps.map(p => chain.includes(p.id) ? { ...p, machineId: dc } : p)
  );

  // persist & update API
  chain.forEach((id,i) => {
    const job = newDst.find(j=>j.id===id);
    job.machineId = dc==='queue'?null:dc;
    job.position  = dc==='queue'?null:destination.index+i;
    axios.put(`${API_ROOT}/orders/${id}`, {
      machineId: job.machineId,
      start_date: job.start||'',
      end_date:   job.end  ||'',
      delivery:   job.delivery||'',
      position:   job.position,
      embroidery_start: job.embroidery_start||''
    }).catch(console.error);
  });

  // rebuild columns
  const updated = {
    queue:    { ...columns.queue,    jobs: sortQueue(newSrc.concat(dc==='queue'?newDst:[])) },
    machine1: { ...columns.machine1, jobs: scheduleMachineJobs(dc==='machine1'?newDst:newSrc) },
    machine2: { ...columns.machine2, jobs: scheduleMachineJobs(dc==='machine2'?newDst:newSrc) },
  };

  setColumns(updated);
};

// === Section 9: Render / Return ===
return (
  <div style={{ padding: 16, fontFamily: 'sans-serif', fontSize: 13 }}>
    {/* Add / Sync Buttons */}
    <button
      onClick={() => setShowModal(true)}
      style={{ marginRight: 8, fontSize: 13 }}
    >
      + Add Placeholder
    </button>
    <button onClick={handleSync} style={{ fontSize: 13 }}>
      Sync from Sheet{' '}
      {syncStatus === 'updated' && (
        <span style={{ color: 'green', fontSize: 13 }}>‚úì Updated</span>
      )}
    </button>

    {/* Modal */}
    {showModal && (
      <div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.3)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}
      >
        <div
          style={{
            background: '#fff',
            padding: 20,
            borderRadius: 4,
            minWidth: 300
          }}
        >
          <h3
            style={{
              marginTop: 0,
              textAlign: 'center',
              fontSize: 13
            }}
          >
            Add/Edit Placeholder
          </h3>
          {/* ‚Ä¶modal inputs here‚Ä¶ */}
          <div style={{ textAlign: 'right' }}>
            <button
              onClick={() => setShowModal(false)}
              style={{ marginRight: 8, fontSize: 13 }}
            >
              Cancel
            </button>
            <button onClick={submitPlaceholder} style={{ fontSize: 13 }}>
              Submit
            </button>
          </div>
        </div>
      </div>
    )}

    {/* Drag & Drop Columns */}
    <DragDropContext onDragEnd={onDragEnd}>
      <div style={{ display: 'flex', gap: 16, marginTop: 16 }}>
        {['queue', 'machine1', 'machine2'].map((colId) => {
          const col = columns[colId] || {};
          const jobs = Array.isArray(col.jobs) ? col.jobs : [];

          // build linked segments
          const segments = [];
          let i = 0;
          while (i < jobs.length) {
            const chain = getChain(jobs, jobs[i].id);
            const len = chain.length > 1 ? chain.length : 1;
            segments.push({ start: i, len });
            i += len;
          }

          const GAP = 6,
            OUTLINE = 6;

          return (
            <Droppable key={colId} droppableId={colId}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    position: 'relative',
                    border: '1px solid #ccc',
                    borderRadius: 4,
                    width: colId === 'queue' ? 260 : 300,
                    minHeight: 400,
                    padding: 12,
                    background: '#fafafa'
                  }}
                >
                  <h4
                    style={{
                      textAlign: 'center',
                      margin: '8px 0',
                      fontSize: 13
                    }}
                  >
                    {col.title}
                  </h4>

                  {segments.map((seg, si) => (
                    <div
                      key={si}
                      style={{
                        position: 'relative',
                        marginBottom: GAP,
                        zIndex: seg.len > 1 ? 3 : 1,
                        outline:
                          seg.len > 1 ? `${OUTLINE}px solid #0288d1` : undefined,
                        outlineOffset: seg.len > 1 ? '0px' : undefined,
                        borderRadius: 4
                      }}
                    >
                      {jobs
                        .slice(seg.start, seg.start + seg.len)
                        .map((job, jIdx) => {
                          const idx = seg.start + jIdx;
                          const isPh = job.id.startsWith('ph-');
                          const isSoft = job.due_type === 'Soft Date';
                          const base = isPh
                            ? LIGHT_YELLOW
                            : isSoft
                            ? LIGHT_GREY
                            : LIGHT_PURPLE;
                          let bg = base,
                            bCol = isPh
                              ? DARK_YELLOW
                              : isSoft
                              ? DARK_GREY
                              : DARK_PURPLE;
                          if (job.isLate) {
                            bg = `repeating-linear-gradient(45deg, ${base}, ${base} 6px, rgba(255,0,0,0.5) 6px, rgba(255,0,0,0.5) 12px)`;
                            bCol = 'red';
                          }
                          const dt0 = (job.due_type || '')[0] || '';

                          return (
                            <Draggable
                              key={job.id}
                              draggableId={job.id}
                              index={idx}
                            >
                              {(prov) => (
                                <div
                                  ref={prov.innerRef}
                                  {...prov.draggableProps}
                                  {...prov.dragHandleProps}
                                  style={{
                                    position: 'relative',
                                    display: 'grid',
                                    gridTemplateColumns: '1fr auto',
                                    gridTemplateRows: 'auto auto auto auto',
                                    columnGap: 6,
                                    rowGap: 4,
                                    padding: '6px 28px 6px 6px',
                                    margin: `0 0 ${
                                      jIdx < seg.len - 1 ? GAP : 0
                                    }px 0`,
                                    background: bg,
                                    border: `2px solid ${bCol}`,
                                    borderRadius: 4,
                                    zIndex: 2,
                                    ...prov.draggableProps.style
                                  }}
                                >
                                  {jIdx === 0 && seg.len > 1 && (
                                    <div
                                      style={{
                                        position: 'absolute',
                                        top: 0,
                                        right: 0,
                                        width: 4,
                                        height: '100%',
                                        background: '#0288d1',
                                        zIndex: 4
                                      }}
                                    />
                                  )}

                                  {/* Row1 Col1: ID + Company */}
                                  <span
                                    style={{
                                      gridRow: 1,
                                      gridColumn: 1,
                                      display: 'inline-block',
                                      background: base,
                                      padding: '0 4px',
                                      borderRadius: 4,
                                      whiteSpace: 'nowrap',
                                      overflow: 'hidden',
                                      textOverflow: 'ellipsis',
                                      fontSize: 13,
                                      fontWeight: 'bold'
                                    }}
                                  >
                                    <span
                                      style={{
                                        display: 'inline-block',
                                        width: 20,
                                        height: 20,
                                        borderRadius: '50%',
                                        background: '#000',
                                        color: base,
                                        lineHeight: '20px',
                                        textAlign: 'center',
                                        fontSize: isPh ? 13 : 11,
                                        fontWeight: 'bold',
                                        marginRight: 4
                                      }}
                                    >
                                      {isPh ? '*' : job.id}
                                    </span>
                                    {job.company}
                                  </span>

                                  {/* Row1 Col2: Quantity */}
                                  <span
                                    style={{
                                      gridRow: 1,
                                      gridColumn: 2,
                                      justifySelf: 'end',
                                      display: 'inline-block',
                                      background: base,
                                      padding: '0 4px',
                                      borderRadius: 4,
                                      fontSize: 15,
                                      fontWeight: 'bold',
                                      whiteSpace: 'nowrap',
                                      overflow: 'hidden',
                                      textOverflow: 'ellipsis'
                                    }}
                                  >
                                    {job.quantity}
                                  </span>

                                  {/* Row2 Col1: Design */}
                                  <span
                                    style={{
                                      gridRow: 2,
                                      gridColumn: 1,
                                      display: 'inline-block',
                                      background: base,
                                      padding: '0 4px',
                                      borderRadius: 4,
                                      whiteSpace: 'nowrap',
                                      overflow: 'hidden',
                                      textOverflow: 'ellipsis',
                                      fontSize: 13
                                    }}
                                  >
                                    {job.design?.slice(0, 22)}
                                  </span>

                                  {/* Row2 Col2: Start */}
                                  {job.start && (
                                    <span
                                      style={{
                                        gridRow: 2,
                                        gridColumn: 2,
                                        justifySelf: 'end',
                                        display: 'inline-block',
                                        background: BUBBLE_START,
                                        padding: '0 4px',
                                        borderRadius: 10,
                                        whiteSpace: 'nowrap',
                                        fontSize: 13
                                      }}
                                    >
                                      {job.start}
                                    </span>
                                  )}

                                  {/* Row3 Col1: EED */}
                                  <span
                                    style={{
                                      gridRow: 3,
                                      gridColumn: 1,
                                      display: 'inline-block',
                                      background: BUBBLE_END,
                                      padding: '0 4px',
                                      borderRadius: 4,
                                      whiteSpace: 'nowrap',
                                      overflow: 'hidden',
                                      textOverflow: 'ellipsis',
                                      fontSize: 13
                                    }}
                                  >
                                    {`EED: ${fmtMMDD(
                                      subWorkDays(parseDueDate(job.due_date), 6)
                                    )}`}
                                  </span>

                                  {/* Row3 Col2: End */}
                                  {job.end && (
                                    <span
                                      style={{
                                        gridRow: 3,
                                        gridColumn: 2,
                                        justifySelf: 'end',
                                        display: 'inline-block',
                                        background: BUBBLE_END,
                                        padding: '0 4px',
                                        borderRadius: 10,
                                        whiteSpace: 'nowrap',
                                        fontSize: 13
                                      }}
                                    >
                                      {job.end}
                                    </span>
                                  )}

                                  {/* Row4 Col1: IHD */}
                                  <span
                                    style={{
                                      gridRow: 4,
                                      gridColumn: 1,
                                      display: 'inline-block',
                                      background: BUBBLE_DELIV,
                                      padding: '0 4px',
                                      borderRadius: 4,
                                      whiteSpace: 'nowrap',
                                      fontSize: 13
                                    }}
                                  >
                                    {`IHD: ${fmtMMDD(job.due_date)}`}
                                  </span>

                                  {/* Row4 Col2: Delivery */}
                                  {job.delivery && (
                                    <span
                                      style={{
                                        gridRow: 4,
                                        gridColumn: 2,
                                        justifySelf: 'end',
                                        display: 'inline-block',
                                        background: BUBBLE_DELIV,
                                        padding: '0 4px',
                                        borderRadius: 10,
                                        whiteSpace: 'nowrap',
                                        fontSize: 13
                                      }}
                                    >
                                      {job.delivery}
                                    </span>
                                  )}

                                  {/* Top-right tab: Due Type initial */}
                                  <div
                                    style={{
                                      position: 'absolute',
                                      top: 0,
                                      right: 0,
                                      width: 28,
                                      height: 28,
                                      display: 'flex',
                                      alignItems: 'center',
                                      justifyContent: 'center',
                                      background: base,
                                      borderTopRightRadius: 4,
                                      borderBottomLeftRadius: 4,
                                      zIndex: 4,
                                      fontSize: 11,
                                      fontWeight: 'bold'
                                    }}
                                  >
                                    {dt0}
                                  </div>

                                  {/* üîó / ‚ùå Toggle */}
                                  {idx < jobs.length - 1 && (
                                    <div
                                      onClick={() => toggleLink(colId, idx)}
                                      style={{
                                        position: 'absolute',
                                        top: 28,
                                        right: 0,
                                        width: 28,
                                        height: `calc(100% - 28px)`,
                                        display: 'flex',
                                        flexDirection: 'column',
                                        alignItems: 'center',
                                        justifyContent: 'center',
                                        cursor: 'pointer',
                                        background: base,
                                        borderBottomRightRadius: 4,
                                        zIndex: 4
                                      }}
                                    >
                                      {job.linkedTo === jobs[idx + 1]?.id
                                        ? '‚ùå'
                                        : 'üîó'}
                                    </div>
                                  )}

                                  {/* Placeholder edit/delete */}
                                  {job.id.startsWith('ph-') && (
                                    <div
                                      style={{
                                        position: 'absolute',
                                        top: 0,
                                        right: 0,
                                        width: 28,
                                        height: '100%',
                                        display: 'flex',
                                        flexDirection: 'column',
                                        alignItems: 'center',
                                        justifyContent: 'start',
                                        background: base,
                                        borderTopRightRadius: 4,
                                        borderBottomRightRadius: 4,
                                        zIndex: 4
                                      }}
                                    >
                                      <span
                                        onClick={() => editPlaceholder(job)}
                                        style={{
                                          cursor: 'pointer',
                                          fontSize: 12,
                                          margin: 4
                                        }}
                                      >
                                        ‚úé
                                      </span>
                                      <span
                                        onClick={() => removePlaceholder(job.id)}
                                        style={{
                                          cursor: 'pointer',
                                          fontSize: 12,
                                          margin: 4
                                        }}
                                      >
                                        ‚úñ
                                      </span>
                                    </div>
                                  )}
                                </div>
                              )}
                            </Draggable>
                          );
                        })}
                    </div>
                  ))}

                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          );
        })}
      </div>
    </DragDropContext>
  </div>
);

// ‚Üê end of App component
}
